DELIMITER $$
CREATE TRIGGER `reference_delete` BEFORE DELETE ON `website` FOR EACH ROW DELETE FROM reference 
WHERE reference.website_id_child = OLD.website_id OR reference.website_id_parent = OLD.website_id
$$
DELIMITER ;

--
-- Índices para tablas volcadas
--

--
-- Indices de la tabla `reference`
--
ALTER TABLE `reference`
  ADD PRIMARY KEY (`website_id_parent`,`website_id_child`),
  ADD KEY `website_id_child` (`website_id_child`);

--
-- Indices de la tabla `website`
--
ALTER TABLE `website`
  ADD PRIMARY KEY (`website_id`),
  ADD UNIQUE KEY `url` (`url`);
ALTER TABLE `website` ADD FULLTEXT KEY `title` (`title`,`description`,`body`,`keywords`);

--
-- AUTO_INCREMENT de las tablas volcadas
--

--
-- AUTO_INCREMENT de la tabla `website`
--
ALTER TABLE `website`
  MODIFY `website_id` int(11) NOT NULL AUTO_INCREMENT, AUTO_INCREMENT=354;

--
-- Restricciones para tablas volcadas
--

--
-- Filtros para la tabla `reference`
--
ALTER TABLE `reference`
  ADD CONSTRAINT `reference_ibfk_1` FOREIGN KEY (`website_id_parent`) REFERENCES `website` (`website_id`),
  ADD CONSTRAINT `reference_ibfk_2` FOREIGN KEY (`website_id_child`) REFERENCES `website` (`website_id`);
COMMIT;

